package com.example.composedemo.presentation.navigation

import androidx.compose.runtime.Composable
import androidx.compose.ui.Modifier
import androidx.navigation.NavHostController
import androidx.navigation.compose.NavHost
import androidx.navigation.compose.composable
import com.example.composedemo.presentation.ui.MainScreen


sealed class Screens(val route: String) {
    object NewsScreen : Screens("news_screen")
    object ArticleScreen : Screens("category")
    object ArticleDetailsScreen : Screens("article_details_screen")
}
@Composable
fun NavGraph(navController: NavHostController, modifier: Modifier = Modifier) {
    NavHost(
        navController = navController,
        startDestination = "main",
        modifier = modifier
    ) {
        composable("main") {
            MainScreen()
        }
    }
}

