import com.example.composedemo.data.model.Article
import com.example.composedemo.data.preferences.ThemeMode
import com.example.composedemo.presentation.viewmodel.NewsViewModel
import com.example.composedemo.presentation.viewmodel.SettingsViewModel
import com.example.composedemo.presentation.viewmodel.SharedArticleViewModel
import com.example.composedemo.presentation.navigation.Screens
import com.example.composedemo.presentation.ui.components.*
import com.google.accompanist.swiperefresh.SwipeRefresh
import com.google.accompanist.swiperefresh.rememberSwipeRefreshState
import kotlinx.coroutines.delay

@OptIn(ExperimentalMaterial3Api::class, ExperimentalFoundationApi::class)
@Composable
fun EnhancedNewsScreen(
    modifier: Modifier = Modifier,
    newsViewModel: NewsViewModel = hiltViewModel(),
    settingsViewModel: SettingsViewModel = hiltViewModel(),
    sharedArticleViewModel: SharedArticleViewModel = hiltViewModel(),
    navController: NavHostController
) {
    val newsUiState by newsViewModel.uiState.collectAsStateWithLifecycle()
    val settingsUiState by settingsViewModel.uiState.collectAsStateWithLifecycle()

    var viewMode by remember { mutableStateOf(ViewMode.CARD) }
    var showSearch by remember { mutableStateOf(false) }
    var searchQuery by remember { mutableStateOf("") }

    val strings = LanguageStrings(settingsUiState.language)
    val swipeRefreshState = rememberSwipeRefreshState(newsUiState.isLoading)

    // Function to handle article click
    val onArticleClick: (Article) -> Unit = { article ->
        // Set the article in shared viewmodel first
        sharedArticleViewModel.setSelectedArticle(article)
        // Then navigate
        navController.navigate("${Screens.ArticleDetailsScreen.route}/${article.articleId}")
    }

    LaunchedEffect(settingsUiState.pageSize) {
        // Reload news when page size changes
        newsViewModel.getLatestNews(size = settingsUiState.pageSize)
    }

    LaunchedEffect(settingsUiState.autoRefresh) {
        if (settingsUiState.autoRefresh) {
            while (true) {
                delay(300000) // 5 minutes
                newsViewModel.getLatestNews(size = settingsUiState.pageSize)
            }
        }
    }

    Scaffold(
        topBar = {
            if (showSearch) {
                SearchTopBar(
                    query = searchQuery,
                    onQueryChange = { searchQuery = it },
                    onSearch = {
                        newsViewModel.searchNews(searchQuery, size = settingsUiState.pageSize)
                    },
                    onCloseSearch = {
                        showSearch = false
                        searchQuery = ""
                        newsViewModel.getLatestNews(size = settingsUiState.pageSize)
                    },
                    placeholder = strings["search"] ?: "Search news..."
                )
            } else {
                EnhancedToolbar(
                    title = strings["latest_news"] ?: "Latest News",
                    navController = navController,
                    currentLanguage = settingsUiState.language,
                    currentTheme = settingsUiState.themeMode,
                    onLanguageChange = { settingsViewModel.setLanguage(it) },
                    onThemeChange = { settingsViewModel.setThemeMode(it) },
                    onSettingsClick = {
                        navController.navigate("settings")
                    },
                    onNotificationClick = {
                        // Handle notifications
                    }
                ) {
                    // Search button
                    IconButton(onClick = { showSearch = true }) {
                        Icon(
                            Icons.Default.Search,
                            contentDescription = strings["search"],
                            tint = MaterialTheme.colorScheme.onSurface
                        )
                    }

                    // View mode selector
                    ViewModeSelector(
                        currentMode = viewMode,
                        onModeChange = { viewMode = it }
                    )
                }
            }
        },
        floatingActionButton = {
            if (!newsUiState.isLoading) {
                ExtendedFloatingActionButton(
                    onClick = { newsViewModel.getLatestNews(size = settingsUiState.pageSize) },
                    icon = { Icon(Icons.Default.Refresh, contentDescription = null) },
                    text = { Text(strings["refresh"] ?: "Refresh") },
                    containerColor = MaterialTheme.colorScheme.primaryContainer
                )
            }
        }
    ) { innerPadding ->
        SwipeRefresh(
            state = swipeRefreshState,
            onRefresh = { newsViewModel.getLatestNews(size = settingsUiState.pageSize) },
            modifier = Modifier.padding(innerPadding)
        ) {
            when {
                newsUiState.isLoading && newsUiState.articles.isEmpty() -> {
                    when (viewMode) {
                        ViewMode.CARD -> ShimmerHorizontalCards()
                        ViewMode.LIST -> ShimmerNewsList()
                        ViewMode.MAGAZINE -> ShimmerMagazineView()
                    }
                }

                newsUiState.error != null -> {
                    EnhancedErrorCard(
                        error = newsUiState.error!!,
                        onRetry = { newsViewModel.getLatestNews(size = settingsUiState.pageSize) },
                        strings = strings
                    )
                }

                newsUiState.articles.isNotEmpty() -> {
                    when (viewMode) {
                        ViewMode.CARD -> {
                            EnhancedSwipeableCards(
                                articles = newsUiState.articles,
                                textSize = settingsUiState.textSize,
                                imageQuality = settingsUiState.imageQuality,
                                onArticleClick = onArticleClick,
                                strings = strings
                            )
                        }
                        ViewMode.LIST -> {
                            EnhancedNewsListView(
                                articles = newsUiState.articles,
                                textSize = settingsUiState.textSize,
                                imageQuality = settingsUiState.imageQuality,
                                onArticleClick = onArticleClick,
                                strings = strings
                            )
                        }
                        ViewMode.MAGAZINE -> {
                            MagazineStyleView(
                                articles = newsUiState.articles,
                                textSize = settingsUiState.textSize,
                                imageQuality = settingsUiState.imageQuality,
                                onArticleClick = onArticleClick,
                                strings = strings
                            )
                        }
                    }
                }

                else -> {
                    EmptyNewsState(
                        onRefresh = { newsViewModel.getLatestNews(size = settingsUiState.pageSize) },
                        strings = strings
                    )
                }
            }
        }
    }
}

@Composable
private fun ViewModeSelector(
    currentMode: ViewMode,
    onModeChange: (ViewMode) -> Unit
) {
    var showMenu by remember { mutableStateOf(false) }

    Box {
        IconButton(onClick = { showMenu = true }) {
            Icon(
                when (currentMode) {
                    ViewMode.CARD -> Icons.Default.ViewModule
                    ViewMode.LIST -> Icons.Default.ViewList
                    ViewMode.MAGAZINE -> Icons.Default.ViewQuilt
                },
                contentDescription = "View Mode",
                tint = MaterialTheme.colorScheme.onSurface
            )
        }

        DropdownMenu(
            expanded = showMenu,
            onDismissRequest = { showMenu = false }
        ) {
            ViewMode.values().forEach { mode ->
                DropdownMenuItem(
                    text = {
                        Text(
                            when (mode) {
                                ViewMode.CARD -> "Card View"
                                ViewMode.LIST -> "List View"
                                ViewMode.MAGAZINE -> "Magazine View"
                            }
                        )
                    },
                    onClick = {
                        onModeChange(mode)
                        showMenu = false
                    },
                    leadingIcon = {
                        Icon(
                            when (mode) {
                                ViewMode.CARD -> Icons.Default.ViewModule
                                ViewMode.LIST -> Icons.Default.ViewList
                                ViewMode.MAGAZINE -> Icons.Default.ViewQuilt
                            },
                            contentDescription = null
                        )
                    },
                    trailingIcon = {
                        if (mode == currentMode) {
                            Icon(Icons.Default.Check, contentDescription = null)
                        }
                    }
                )
            }
        }
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
private fun SearchTopBar(
    query: String,
    onQueryChange: (String) -> Unit,
    onSearch: () -> Unit,
    onCloseSearch: () -> Unit,
    placeholder: String
) {
    TopAppBar(
        title = {
            OutlinedTextField(
                value = query,
                onValueChange = onQueryChange,
                placeholder = { Text(placeholder) },
                modifier = Modifier.fillMaxWidth(),
                singleLine = true,
                trailingIcon = {
                    if (query.isNotEmpty()) {
                        IconButton(onClick = { onQueryChange("") }) {
                            Icon(Icons.Default.Clear, contentDescription = "Clear")
                        }
                    }
                }
            )
        },
        navigationIcon = {
            IconButton(onClick = onCloseSearch) {
                Icon(Icons.Default.ArrowBack, contentDescription = "Close Search")
            }
        },
        actions = {
            IconButton(onClick = onSearch) {
                Icon(Icons.Default.Search, contentDescription = "Search")
            }
        }
    )
}

