package com.example.composedemo.presentation.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.composedemo.data.model.Article
import com.example.composedemo.data.repository.NewsRepository
import com.example.composedemo.data.local.ArticleCache
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.launch
import javax.inject.Inject

@HiltViewModel
class ArticleDetailsViewModel @Inject constructor(
    private val newsRepository: NewsRepository,
    private val articleCache: ArticleCache
) : ViewModel() {

    private val _uiState = MutableStateFlow(ArticleDetailsUiState())
    val uiState: StateFlow<ArticleDetailsUiState> = _uiState.asStateFlow()

    fun getArticleDetails(articleId: String) {
        viewModelScope.launch {
            _uiState.value = _uiState.value.copy(isLoading = true, error = null)

            // First check cache
            val cachedArticle = articleCache.getArticle(articleId)
            if (cachedArticle != null) {
                println("ArticleDetailsViewModel: Found article in cache: ${cachedArticle.title}")
                _uiState.value = _uiState.value.copy(
                    isLoading = false,
                    article = cachedArticle,
                    error = null
                )
                searchRelatedArticles(cachedArticle.keywords)
                return@launch
            }

            // If not in cache, search all cached articles
            val allCached = articleCache.getAllCachedArticles()
            val article = allCached.find { it.articleId == articleId }

            if (article != null) {
                println("ArticleDetailsViewModel: Found article in all cached: ${article.title}")
                _uiState.value = _uiState.value.copy(
                    isLoading = false,
                    article = article,
                    error = null
                )
                searchRelatedArticles(article.keywords)
            } else {
                println("ArticleDetailsViewModel: Article not found in cache, ID: $articleId")
                _uiState.value = _uiState.value.copy(
                    isLoading = false,
                    error = "Article not found. Please go back and try again."
                )
            }
        }
    }

    fun setArticle(article: Article) {
        _uiState.value = _uiState.value.copy(
            isLoading = false,
            article = article,
            error = null
        )
        searchRelatedArticles(article.keywords)
    }

    fun searchRelatedArticles(keywords: List<String>?) {
        if (keywords.isNullOrEmpty()) return

        viewModelScope.launch {
            try {
                val searchQuery = keywords.joinToString(" ")
                newsRepository.getLatestNews(query = searchQuery, size = 5).collect { result ->
                    result.fold(
                        onSuccess = { newsResponse ->
                            _uiState.value = _uiState.value.copy(
                                relatedArticles = newsResponse.results
                            )
                        },
                        onFailure = { /* Handle silently for related articles */ }
                    )
                }
            } catch (e: Exception) {
                // Handle silently for related articles
            }
        }
    }
}

data class ArticleDetailsUiState(
    val isLoading: Boolean = false,
    val article: Article? = null,
    val relatedArticles: List<Article> = emptyList(),
    val error: String? = null
)

